.text-center {
  text-align: center;
}
.float-left {
  float: left;
}
.float-right {
  float: right;
}
.sidebar-inner {
  border-radius: 5px;
}
.sidebar-inner .site-author-name {
  color: $purple-light;
}
.sidebar-inner .exturl {
  border-bottom: 0px;
}
.sidebar-inner .links-of-blogroll-item {
  border: 1px solid #eee;
  border-radius: 20px;
  background: #eee;
}
.sidebar-inner .links-of-blogroll-item:hover {
  border: 1px solid $blue-light;
  background: #fff;
}
.sidebar-inner .links-of-blogroll-item:hover a {
  color: $blue-light;
}
.header-inner {
  border-radius: 0 0 $radius $radius;
}
.header-inner .main-menu {
  color: $blue-bright;
}
.pagination {
  border-radius: $radius;
}
.main-inner .post-block {
  border-radius: $radius;
}
.main-inner article a {
  color: $purple-blue;
  border-bottom: 0;
}
.main-inner article a:hover {
  color: $purple-light;
}
.main-inner h2 a {
  color: $blue;
  border-bottom: 0;
}
.main-inner h2 a:hover {
  color: $blue-light;
}
.main-inner .post-button a:hover {
  color: #fff;
  background: $blue;
}
.collection-header {
  color: $blue-light;
}
.posts-collapse::before {
  background: $blue-bright;
}
.posts-collapse .collection-title::before {
  background: $blue-light;
}
.posts-collapse .collection-year::before {
  background: $blue-light;
}
.posts-collapse .post-header::before {
  background: $blue-light;
}
.posts-collapse .post-header:hover::before {
  background: $purple-light;
}
body {
  background: #fff url("../images/bg-3.webp") no-repeat;
  background-size: cover;
  background-attachment: fixed;
  background-position: top;
}
.main-inner .post-block {
  background: rgba(255,255,255,0.95);
}
.site-brand-container {
  background-image: linear-gradient(120deg, #a1c4fd 0%, #c2e9fb 100%);
}
.site-title {
  color: $orange;
}
.site-subtitle {
  color: $pink-light;
}
.category-all-page .category-all-title {
  text-align: center;
}
.category-all-page .category-all {
  margin-top: 20px;
}
.category-all-page .category-list {
  list-style: none;
  margin: 0;
  padding: 0;
  text-align: center;
}
.category-all-page .category-list-item {
  border-radius: $radius;
  margin: 15px 20px;
  padding: 20px 20px 20px 30px;
  height: 40px;
  line-height: 40px;
  width: 40%;
  max-width: 280px;
  min-width: 200px;
  text-align: left;
  display: inline-block;
  background: $lavender;
  box-shadow: 0 1px 2px rgba(0,0,0,0.07), 0 2px 4px rgba(0,0,0,0.07), 0 4px 8px rgba(0,0,0,0.07), 0 8px 16px rgba(0,0,0,0.07), 0 16px 32px rgba(0,0,0,0.07), 0 32px 64px rgba(0,0,0,0.07);
}
.category-all-page .category-list-count {
  display: block;
  position: relative;
  float: right;
  height: 30px;
  width: 50px;
  line-height: 30px;
  top: 50%;
  transform: translateY(-50%);
  border-radius: 20px;
  text-align: center;
  color: $orange;
  background: $blue-bright;
  box-shadow: 0 1px 2px rgba(0,0,0,0.07), 0 2px 4px rgba(0,0,0,0.07), 0 4px 8px rgba(0,0,0,0.07), 0 8px 16px rgba(0,0,0,0.07), 0 16px 32px rgba(0,0,0,0.07), 0 32px 64px rgba(0,0,0,0.07);
}
.category-all-page .category-list-count:before {
  content: '';
}
.category-all-page .category-list-count:after {
  content: '';
}
.category-all-page .category-list-child {
  padding-left: 10px;
}
.category-list-link {
  border-bottom: 0;
}
.post-button {
  margin-top: 30px;
  text-align: center;
}
.post-button .btn {
  margin: 0 4px 8px 4px;
  padding: 0 20px;
  color: $blue;
  border: 2px $blue solid;
  font-size: 15px;
  border-radius: 16px;
  line-height: 2;
}
.post-copyright {
  border-left: 3px solid $orange-bright;
  background-color: $purple-little;
}
.post-copyright .fa {
  color: $orange-light;
}
figcaption {
  text-align: center;
}
.highlight {
  box-shadow: 0 10px 15px 0 rgba(0,0,0,0.3);
}
code,
kbd,
.highlight,
pre {
  background: #f8f8f8;
}
.post-body {
  counter-reset: h2;
}
.post-body h1 a,
.post-body h2 a,
.post-body h3 a,
.post-body h4 a,
.post-body h5 a,
.post-body h6 a {
  color: $blue;
  border-bottom: 0;
}
.post-body h1 a:hover,
.post-body h2 a:hover,
.post-body h3 a:hover,
.post-body h4 a:hover,
.post-body h5 a:hover,
.post-body h6 a:hover {
  color: $blue-light;
}
.post-body h1 {
  counter-reset: h2;
}
.post-body h2 {
  counter-reset: h3;
}
.post-body h3 {
  counter-reset: h4;
}
.post-body h4 {
  counter-reset: h5;
}
.post-body h5 {
  counter-reset: h6;
}
.post-body h2:before {
  counter-increment: h2;
  content: counter(h2) '. ';
}
.post-body h3:before {
  counter-increment: h3;
  content: counter(h2) '.' counter(h3) '. ';
}
.post-body h4:before {
  counter-increment: h4;
  content: counter(h2) '.' counter(h3) '.' counter(h4) '. ';
}
.post-body h5:before {
  counter-increment: h5;
  content: counter(h2) '.' counter(h3) '.' counter(h4) '.' counter(h5) '. ';
}
.post-body h6:before {
  counter-increment: h6;
  content: counter(h2) '.' counter(h3) '.' counter(h4) '.' counter(h5) '.' counter(h6) '. ';
}
.nav {
  counter-reset: h2;
}
.nav .nav-item.nav-level-1 {
  counter-reset: h2;
}
.nav .nav-item.nav-level-2 {
  counter-reset: h3;
}
.nav .nav-item.nav-level-3 {
  counter-reset: h4;
}
.nav .nav-item.nav-level-4 {
  counter-reset: h5;
}
.nav .nav-item.nav-level-5 {
  counter-reset: h6;
}
.nav .nav-item.nav-level-2 a span:before {
  counter-increment: h2;
  content: counter(h2) '. ';
}
.nav .nav-item.nav-level-3 a span:before {
  counter-increment: h3;
  content: counter(h2) '.' counter(h3) '. ';
}
.nav .nav-item.nav-level-4 a span:before {
  counter-increment: h4;
  content: counter(h2) '.' counter(h3) '.' counter(h4) '. ';
}
.nav .nav-item.nav-level-5 a span:before {
  counter-increment: h5;
  content: counter(h2) '.' counter(h3) '.' counter(h4) '.' counter(h5) '. ';
}
.nav .nav-item.nav-level-6 a span:before {
  counter-increment: h6;
  content: counter(h2) '.' counter(h3) '.' counter(h4) '.' counter(h5) '.' counter(h6) '. ';
}
h1,
.post-body h2,
.post-body h3,
.post-body h4,
.post-body h5,
.post-body h6 {
  color: $blue;
}
